                     var cDateObj="";
 var cMonthOnly=false;
 var cYearByAD=true;
 var cCurDay="";
 var cDateCopyObj="";
 var cMaxDate="";
 var cMinDate="";
 
//**********************************************************
//* 檢核日期
//**********************************************************
function isDate(pYY,pMM,pDD) {
  pMM --;
  var sDate = new Date(pYY,pMM,pDD);
  if (parseInt(pDD,10) == sDate.getDate()) {
     return true;
  } else {
     return false;
  }      
}

//**********************************************************
//* 設定西元年
//**********************************************************
function setYears() {
   var sYobj = $get("selYear");
   var sMobj = $get("selMonth");
   var sItem;
   var sValu=0;
   var sNow = new Date();
   //var sYear = sNow.getFullYear() - 90;   
   var sYear = sNow.getFullYear() - 3; 
   
   //---------------------------* 預設10個年(系統年之前3及後7年)
   sYobj.options.length = 0;  //* 先清空
   //for(var i=0; i<=100; i++) {
   for(var i=0; i<=10; i++) {
     sValu = sYear + i - (cYearByAD ? 0:1911);
     sItem = new Option(sValu,sValu);
     sYobj.options.add(sItem);
   }
   
   //---------------------------* 設定 年月日之位置   
   if($get(cDateObj).value =='') { 
       sYobj.selectedIndex = 3;
      sMobj.selectedIndex = sNow.getMonth();   //本月
      cCurDay = sNow.getDate();                //本'日'-only
      
   } else {
      var sAry = $get(cDateObj).value.split("/");
      sValu = parseInt(sAry[0],10) + (cYearByAD ? 0:1911);
      sValu = sNow.getFullYear() - sValu; 
      sYobj.selectedIndex = 3 - sValu;
      sMobj.selectedIndex = parseInt(sAry[1],10) -1 ; 
      if( ! cMonthOnly) { cCurDay = sAry[2]; }  
   }
   
   if(cMonthOnly) {
      $get('btnOK').style.display = "";      //顯示 確認鍵
      $get('tblDay').style.display = "none"; //隱藏 日期
   } else {
      $get('btnOK').style.display = "none"; 
      $get('tblDay').style.display = ""; 
      setMonthDays(); //設定該年月之日期
   }
   // $get('tblCalendar').style.zIndex = 199; //最上層顯示
}

