//**********************************************************
//* 設定日期格式
//**********************************************************
function setDayValue(pDay, pIdx, pCmp, pLimit) {
   var sStr;
   if(pIdx < pCmp) { 
       sStr = " ";
    } else if ( ! pLimit) {
    sStr = "<a href=javascript:void(0) onclick='retDate(" + pDay + ")' " + pDay + " ";
    } else {  
    //disable area
    sStr = "" + pDay + " ";    
    }
    return sStr;
}

//**********************************************************
//* 設定整月日期
//**********************************************************
function setMonthDays() {
    if(cMonthOnly) { return; }
    var sStr, I, J, K ;

    sStr = " "
         + ""
         + "
 "   //星期顏色
         + "Su "
         + "Mo "
         + "Tu "
         + "We "
         + "Th "
         + "Fr "
         + "Sa "
         + " "
         + ""
         + ""
 
    var sYear = parseInt($get("selYear").value,10) + (cYearByAD ? 0:1911);
    var sMonth = $get("selMonth").value;
    var sDate = new Date(sYear,sMonth-1,"01");
    I = sDate.getDay();   //取得星期,星期日 = 0,星期一 = 1,星期二 = 2,星期三 = 3,星期四 = 4,星期五 = 5, 星期六 = 6
    
    J = 1;
    
    var sLimit = false;
    var sMinDate = '';
    var sMaxDate = '';
    if (cMinDate != '') { sMinDate = $get(cMinDate).value; }
    if (cMaxDate != '') { sMaxDate = $get(cMaxDate).value; }
    
    while(true) {
        sStr = sStr + "
 ";
       
       for(K=0; K<=6; K++) {      
          sLimit = false;
          if (sMinDate != '') { //限制低於某個時間就不能再點選日期 (2008/05/08 NEC 杜)
              if (sMinDate > (sYear + '/' + sMonth + '/' + ((J<10) ? "0":"") + J)) { sLimit = true;}
          } 
          if (sMaxDate != '') { //限制超過某個時間就不能再點選日期 (2008/05/08 NEC 杜)
              if (sMaxDate < (sYear + '/' + sMonth + '/' + ((J<10) ? "0":"") + J)) { sLimit = true;}
          } 
       
          sStr = sStr + setDayValue(J, K, I, sLimit);
          if(K >= I) {
             J++;
             if(J >= 28){
               if (! isDate(sYear,sMonth,J)) { I = 9; }
             }
          }
        }
 
        sStr = sStr + " ";
        if( I == 9 ) { break; }
        I = 0;
    }

    sStr = sStr + "";
    //alert(sStr);
    $get("tblDay").innerHTML = sStr;
}

//**********************************************************
//* 回傳日期 /年月
//**********************************************************
function retDate(pSelDay) {
   if(cMonthOnly) {
     $get(cDateObj).value= $get("selYear").value + '/' + $get("selMonth").value ;
   } else {
     $get(cDateObj).value = $get("selYear").value + '/' + $get("selMonth").value + '/' + ((pSelDay<10) ? "0":"") + pSelDay;
   }     
   
   if (cDateCopyObj != "") { 
      if ($get(cDateCopyObj).value =='') { $get(cDateCopyObj).value = $get(cDateObj).value; }
   }
   
   HideCalendar();
}

//**********************************************************
//* 清除日期
//**********************************************************
function ClearDate() {
   $get(cDateObj).value= '';   
   HideCalendar();
}

//**********************************************************
//* 隱藏日期選擇視窗
//**********************************************************
function HideCalendar() {
   $get('divCalendar').style.visibility = "hidden";
   $get('divCalendar').style.display = "none";           //janewang 20091129
}

//**********************************************************
//* 開啟日期選擇視窗
//* 參數1:pDateObj => 日期物件之ClientID (須完整)
//* 參數2:pYearByAD => 西元年(true) / 民國年(false)
//* 參數3:pMonthOnly => 只顯示年月
//* 參數4:pDateCopyObj => 設定另一個日期物件為同選取值
//* 參數5:pMaxDate => 超過此時間就不能再點選日期(年/MM/dd)
//* 參數6:pMinDate => 低於此時間就不能再點選日期(年/MM/dd)
//**********************************************************
function ShowCalendar(pDateObj, pYearByAD, pMonthOnly, pDateCopyObj, pMaxDate, pMinDate) {
   // if (sYobj.options.length > 0) { return; } 
   cMinDate = '';
   cMaxDate = '';
   cDateObj = pDateObj;
   cYearByAD = pYearByAD;
   cMonthOnly = pMonthOnly;
   if (pDateCopyObj != null) { if (pDateCopyObj != '') cDateCopyObj = pDateCopyObj;  } 
   if (pMinDate != null) { cMinDate = pMinDate;  } 
   if (pMaxDate != null) { cMaxDate = pMaxDate;  }
   
   setYears();
   //$get('txtType').value = (cYearByAD ? "西元" : "民國");
   $get('txtType').value = (cYearByAD ? "" : "民國");
   $get('divCalendar').style.visibility = "visible"; 
   $get('divCalendar').style.display = "inline";                   //janewang 20091129)
   SetPosXY();
}

//**********************************************************
//* 設定日期起始位置
//**********************************************************
function SetPosXY() {
   var sObj = $get(cDateObj);
   var X = sObj.offsetTop;
   var Y = sObj.offsetLeft;

   while(sObj = sObj.offsetParent) {
      X += sObj.offsetTop;
      Y += sObj.offsetLeft;
   }
   $get('divCalendar').style.top = X+15;  
   $get('divCalendar').style.left = Y-12;

}

