# # given ans (unionByRank) Beats 66.75%
# 看起來這個 case unionByRank 跟普通的 Union 差不多
class UF:
    def __init__(self, n):
        self.id = list(range(n))
        self.rank = [0]*n

    def unionByRank(self, u, v) :
        i = self.find(u)
        j = self.find(v)
        if (i == j) :
            return
        if (self.rank[i] < self.rank[j]) :
            self.id[i] = self.id[j]
        elif (self.rank[i] > self.rank[j]) :
            self.id[j] = self.id[i]
        else :
            self.id[i] = self.id[j]
            self.rank[j] += 1

    def find(self, u) :
        if self.id[u] != u:
            self.id[u] = self.find(self.id[u])
        return self.id[u]
        
class Solution:
    def distanceLimitedPathsExist(self, n: int, edgeList: List[List[int]], queries: List[List[int]]) -> List[bool]:
        ans = [False]*len(queries)
        qs = [ query + [indx] for indx , query in enumerate(queries) ]
        uf = UF(n)

        # 用 limit 排序
        qs.sort(key=lambda x : x[2])
        edgeList.sort(key=lambda x : x[2])

        i = 0; # i : edgeList's index
        for q in qs :
            # 每次只先相連小於 edgeList 的 limit 的 edge
            while i < len(edgeList) and edgeList[i][2] < q[2] :
                uf.unionByRank( edgeList[i][0], edgeList[i][1] )
                i+=1
            # 如果是同一個集合代表此兩點在 limit 的限制下有相連
            if uf.find(q[0]) == uf.find(q[1]) :
                ans[q[3]] = True
        return ans
    
# given ans (Normal Union) Beats 70.76%
class UF:
    def __init__(self, n):
        self.id = list(range(n))

    def union(self, u, v):
        i = self.find(u)
        j = self.find(v)
        if i == j:
            return
        self.id[i] = j

    def find(self, u):
        if self.id[u] != u:
            self.id[u] = self.find(self.id[u])
        return self.id[u]
        
class Solution:
    def distanceLimitedPathsExist(self, n: int, edgeList: List[List[int]], queries: List[List[int]]) -> List[bool]:
        ans = [False]*len(queries)
        qs = [ query + [indx] for indx , query in enumerate(queries) ]
        uf = UF(n)

        # 用 limit 排序
        qs.sort(key=lambda x : x[2])
        edgeList.sort(key=lambda x : x[2])

        i = 0; # i : edgeList's index
        for q in qs :
            # 每次只先相連小於 edgeList 的 limit 的 edge
            while i < len(edgeList) and edgeList[i][2] < q[2] :
                uf.union( edgeList[i][0], edgeList[i][1] )
                i+=1
            # 如果是同一個集合代表此兩點在 limit 的限制下有相連
            if uf.find(q[0]) == uf.find(q[1]) :
                ans[q[3]] = True
        return ans

